import React, {useState} from 'react';
import {
    StyleSheet,
    TouchableHighlight,
    View, Image
} from 'react-native';

const Checkbox = (key, ) => {
    const [value, setValue] = useState(false)

    return (
        <TouchableHighlight onPress={(value) => {setValue(!value)}} value={value} key={key}>
            <View style={styles.CheckboxContainer}>
                {value ? <Image style={styles.SelectSymbol} source={require('../public/images/selectSymbol.png')}/> : <View></View>}
            </View>
        </TouchableHighlight>
    );

}

const styles = StyleSheet.create(
    {
        CheckboxContainer: {
            display: 'inlineFlex',
            border: '3px solid green',
            height: '10px',
            width: '10px',
            padding: 3,
            alignItems: 'center',
            justifyContent: 'center'
        },
        SelectSymbol: {
            display: 'inlineFlex',
        }
    });

/*
Checkbox.propTypes = {
    keyValue: PropTypes.number.isRequired,
    size: PropTypes.number,
    color: PropTypes.string,
    label: PropTypes.string,
    value: PropTypes.string,
    checked: PropTypes.bool,
    labelColor: PropTypes.string,
    checkedObjArr: PropTypes.object.isRequired
}
Checkbox.defaultProps = {
    size: 32,
    checked: false,
    value: 'Default',
    label: 'Default',
    color: '#cecece',
    labelColor: '000000',
}*/

export default Checkbox
