import React, {useEffect, useState} from "react";
import {View, Text, FlatList} from "react-native";
import {requestGetFriends} from "../../core/api/API";
//import Checkbox from "../../assets/checkBox";
import {stylesFriend} from "../../assets/stylesFriends";
import {Checkbox} from "react-native-paper";

const GetFriends = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [message, setMessage] = useState(null)
    const [friends, setFriends] = useState([]);
    const [isSelected, setSelection] = useState(false);
    const [error, setError] = useState(null)
    const [isFriendSelected, setIsFriendSelected] = useState(false)

    useEffect(() => {
        requestGetFriends().then((response) => {
            if (response === 1) {
                setMessage("You don't have friends")
            } else if (response === 0) {
                setMessage("Error")
            } else {
                setFriends(response)
            }
            setIsLoaded(true)
        }).catch(error => {
            setError(error);
        })

    }, [])

    const friend = ({item}) => (
        <View key={item.id} style={stylesFriend.friendsList}>
            <Checkbox onPress={() => {alert("hello")}}
                      status={isFriendSelected ? 'checked' : 'unchecked'}
                      color={'#00ff04'}
                      uncheckColor={'#fff'}
                      testID={item.id}
            />
            <Text>{item.email}</Text>
        </View>
    )

    return (
        <View>
            {isLoaded ? message ? <span>message</span> :
                <FlatList
                    data={friends}
                    renderItem={friend}
                    keyExtractor={item => item.id}
                /> : <span>{error}</span>}

        </View>
    )
}

/*
<ul>
                {friends.map(friend => (
                    <li>
                        <Checkbox/>
                        <Text key={friend.id}>
                            {`${friend.name}, ${friend.email}`}
                        </Text>
                    </li>
                ))}
            </ul>
* */

export default GetFriends
