import React from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity} from "react-native";

const Main = ({navigation}) => {
    const buttonFriendsHandler = () => {
        navigation.navigate('FriendsScreen');
    }
    return (
        <View style={stylesMain.main}>
            <View style={stylesMain.buttonsWrappers}>
                <TouchableOpacity style={stylesMain.buttonMain} onPress={buttonFriendsHandler}>
                    <Text style={stylesMain.buttonMainText}>Friends</Text>
                </TouchableOpacity>
            </View>
            <View style={stylesMain.buttonsWrappers}>
                <TouchableOpacity style={stylesMain.buttonInvite} onPress={buttonFriendsHandler}>
                    <Text style={stylesMain.buttonMainText}>Invite</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const stylesMain = StyleSheet.create({
    main:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'start',
        alignItems: 'center',
        position: 'relative'
    },
    buttonsWrappers:{
        display: 'flex'
        
    },
    inviteWrapper:{
        display:'flex',
        position: 'absolute',
        width: '5%',
        height: '5%',
        top: '45%',
        left: '45%'
    },
    buttonMain:{
        backgroundColor:'#0051ff',
        padding: '10px',
        height: '70px',
        width: '70px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonInvite:{
        backgroundColor:'#0051ff',
        padding: '10px',
        height: '70px',
        width: '70px',
        borderRadius:'50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonMainText:{
        color:'#ffffff'
    }
})

export default Main;
