    checked: PropTypes.bool,
    labelColor: PropTypes.string,
    checkedObjArr: PropTypes.object.isRequired
}
Checkbox.defaultProps = {
    size: 32,
    checked: false,
    value: 'Default',
    label: 'Default',
    color: '#cecece',
    labelColor: '000000',
}*/
const styles = StyleSheet.create(
    {
        CheckboxContainer: {
            flex: 1,
            padding: 22,
            alignItems: 'center',
            justifyContent: 'center',
        },
        showSelectedButton: {
            padding: 20,
            marginTop: 25,
            alignSelf: 'stretch',
            backgroundColor: '#5D52FF'
        },
        buttonText: {
            fontSize: 20,
            color: '#ffffff',
            textAlign: 'center',
            alignSelf: 'stretch'
        },
        selectedUI: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center'
        },
        checkboxTickImg: {
            width: '85%',
            height: '85%',
            tintColor: '#ffffff',
            resizeMode: 'contain'
        },
        uncheckedCheckbox: {
            flex: 1,
            backgroundColor: '#ffffff'
        },
        checkboxLabel: {
            fontSize: 18,
            paddingLeft: 15
        }
    });

export default Checkbox
