import React, {useEffect, useMemo, useState} from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity} from "react-native";
import AddFriend from "./friends/addFriend";
import GetFriends from "./friends/getFriends";
import {useForm} from "react-hook-form";

const FriendsScreen = ({navigation}) => {
    const buttonBackHandler = () => {
        navigation.navigate('MainScreen');
    }

    const [data, setData] = useState(null)

    const defaultValues = useMemo(() => ({
        friends: data?.friends || {}
    }))

    const {
        control,
        reset,
        setValue,
        getValues
    } = useForm({defaultValues, mode: 'onBlur'})

    useEffect(() => {
        reset(defaultValues)
    }, [data])

    return (
        <View style={stylesFriend.Friend}>
            <Text>Friends Page</Text>
            <View style={stylesFriend.butFriendsWrapper}>
                <TouchableOpacity style={stylesFriend.buttonFriends} onPress={buttonBackHandler}>
                    <Text style={stylesFriend.buttonFriendsText}>Back to main page</Text>
                </TouchableOpacity>
            </View>
            <AddFriend/>
            <GetFriends control={control} setValue={setValue}/>
        </View>
    );
}

const stylesFriend = StyleSheet.create({
    Friend:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'start',
        alignItems: 'center',
        position:'relative'
    },
    inputField:{
        padding:'5px',
        margin: '20px',
        border: '2px solid #000'
    },
    butFriendsWrapper:{
        display: 'flex',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center'
    },
    buttonFriends:{
        backgroundColor:'fff',
        border: '3px solid #9d0cff',
        borderRadius: '15px',
        boxSizing: 'borderBox',
        minWidth: '120px',
        fontSize:'20px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriendsText:{
        padding: '10px',
        color: '#000',
        fontSize: '30px'
    },
    checkbox: {
        alignSelf: "center",
    }
})

export default FriendsScreen;
