import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity, Modal, Pressable} from "react-native";
import {getNotifications} from "../core/api/API";
import SendInviteModal from './sendInviteModalScreen';
import GetFriends from "./friends/getFriends";

const Main = ({navigation}) => {

    const [modalVisible, setModalVisible] = useState(false);
    const [isNotifLoaded, setIsNotifLoaded] = useState(false);
    const [notifCount, setNotifCount] = useState(0);
    const [notifications, setNotifications] = useState(0);

    useEffect(() => {
        getNotifications().then((response) => {
            setNotifCount(response.length)
            setNotifications(response)
            console.log(response)
            setIsNotifLoaded(true)
        }).catch(error => {
            console.log(error)
        })

    }, [])

    const buttonFriendsHandler = () => {
        navigation.navigate('FriendsScreen');
    }
    const buttonNotifications = () => {
        navigation.navigate('NotificationsScreen', {response: notifications});
    }
    return (
        <View style={stylesMain.main}>
            <Modal
                animationType="slide"
                transparent={true}
                visible={modalVisible}
                onRequestClose={() => {
                    setModalVisible(!modalVisible);
                }}
            >
                <View style={stylesMain.centeredView}>
                    <View style={styles.modalView}>
                    <Text style={styles.modalText}>Hello World!</Text>
                    <Pressable
                        style={[styles.button, styles.buttonClose]}
                        onPress={() => setModalVisible(!modalVisible)}
                    >
                        <Text style={styles.textStyle}>Hide Modal</Text>
                    </Pressable>
                    </View>
                </View>
            </Modal>
            <Pressable
                onPress={() => setModalVisible(true)}
            >
                <Text>Show Modal</Text>
            </Pressable>
            <View style={stylesMain.butFriendsWrapper}>
                <TouchableOpacity style={stylesMain.buttonFriends} onPress={buttonNotifications}>
                    <Text style={stylesMain.buttonFriendsText}>Notifications</Text>
                    {isNotifLoaded && <Text style={stylesMain.notificationsCount}>{notifCount}</Text>}
                </TouchableOpacity>
            </View>
            <View style={stylesMain.butFriendsWrapper}>
                <TouchableOpacity style={stylesMain.buttonFriends} onPress={buttonFriendsHandler}>
                    <Text style={stylesMain.buttonFriendsText}>Friends</Text>
                </TouchableOpacity>
            </View>
            <View style={stylesMain.butInviteWrapper}>
                <TouchableOpacity style={stylesMain.buttonInvite}>
                    <View style={stylesMain.insideButtonCircle}>
                    <Text style={stylesMain.buttonInviteText}>Invite</Text>
                    </View>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const stylesMain = StyleSheet.create({
    modalView: {
        margin: 20,
        backgroundColor: "white",
        borderRadius: 20,
        padding: 35,
        alignItems: "center",
        shadowColor: "#000",
        shadowOffset: {
            width: 0,
            height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },
    centeredView: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        marginTop: 22
    },
    button: {
        borderRadius: 20,
        padding: 10,
        elevation: 2
    },
    buttonOpen: {
        backgroundColor: "#F194FF",
    },
    buttonClose: {
        backgroundColor: "#2196F3",
    },
    notificationsCount:{
        position: 'absolute',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        height: '22px',
        width: '22px',
        fontSize: '14px',
        border: '2px solid red',
        borderRadius: '50%',
        top:'2px',
        right:'2px'
    },
    main:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'start',
        alignItems: 'center',
        position: 'relative'
    },
    butFriendsWrapper:{
        display: 'flex',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center'
    },
    buttonFriends:{
        position:'relative',
        backgroundColor:'fff',
        border: '3px solid #9d0cff',
        borderRadius: '15px',
        boxSizing: 'borderBox',
        minWidth: '120px',
        fontSize:'20px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriendsText:{
        padding: '10px',
        color: '#000',
        fontSize: '30px'
    },
    butInviteWrapper:{
        width: '100%',
        height:'100%',
        display:'flex',
        alignItems:'center',
        justifyContent: 'center'
    },
    buttonInvite:{
        backgroundColor: '#4A7A4C',
        padding: '10px',
        height: '120px',
        width: '120px',
        borderRadius:'50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        boxShadow: '5px 5px 10px 1px rgba(0, 0, 0, .4)'
    },
    buttonInviteText:{
        color: '#ffeeee',
        fontSize: '30px',
        fontWeight: '500'
    },
    insideButtonCircle:{
        padding:'5px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        width: '90px',
        height:'90px',
        borderRadius: '50%',
        border: '1px solid #14A588',
        backgroundColor: '#14A51C'
    }
})

export default Main;
