import React from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity} from "react-native";

const Main = ({navigation}) => {
    const buttonFriendsHandler = () => {
        navigation.navigate('FriendsScreen');
    }
    return (
        <View style={stylesMain.main}>
            <View style={stylesMain.butFriendsWrapper}>
                <TouchableOpacity style={stylesMain.buttonMain} onPress={buttonFriendsHandler}>
                    <Text style={stylesMain.buttonMainText}>Friends</Text>
                </TouchableOpacity>
            </View>
            <View style={stylesMain.butInviteWrapper}>
                <TouchableOpacity style={stylesMain.buttonInvite} onPress={buttonFriendsHandler}>
                    <Text style={stylesMain.buttonMainText}>Invite</Text>
                </TouchableOpacity>
            </View>
        </View>
    );
}

const stylesMain = StyleSheet.create({
    main:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'start',
        alignItems: 'center',
        position: 'relative'
    },
    butFriendsWrapper:{
        height:'20%',
        display: 'flex',
        width: '100%',
        alignItems: 'center'
    },
    butInviteWrapper:{
        height:'80%',
        display:'flex',
        alignItems:'center'
    },
    buttonMain:{
        backgroundColor:'#9d0cff',
        padding: '10px',
        fontSize:'20px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonInvite:{
        backgroundColor:'#0051ff',
        padding: '10px',
        height: '25%',
        width: '25%',
        fontSize: '30px',
        borderRadius:'50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonMainText:{
        color: '#ffeeee'
    }
})

export default Main;
