import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity} from "react-native";

const FriendsScreen = ({navigation}) => {
    const buttonBackHandler = () => {
        navigation.navigate('MainScreen');
    }


    const [error, setError] = useState(null);
    const [message, setMessage] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);


    useEffect(() => {
        fetch("http://localhost:5000/publi")
            .then(res => res.json())
            .then(
                (result) => {
                    if ("error" in result) {
                        setError(result.error);
                        setIsLoaded(true);
                    } else if ("message" in result) {
                        setMessage(result.message);
                        setIsLoaded(true);
                    } else {
                        setItems(result);
                        console.log(result.error + "AAAAAAAAAAAAAAAAAAAAA");
                        setIsLoaded(true);
                    }
                },
                // Примечание: важно обрабатывать ошибки именно здесь, а не в блоке catch(),
                // чтобы не перехватывать исключения из ошибок в самих компонентах.
                (error) => {

                }
            )
    }, [])

    if (error) {
        return <View><Text>Ошибка: {error.message}</Text></View>;
    } else if (!isLoaded) {
        return <View><Text>Загрузка...</Text></View>;
    } else {
        return (
            <View style={stylesFriend.Friend}>
                <Text>Friends Page</Text>
                <TouchableOpacity style={stylesFriend.buttonFriends} onPress={buttonBackHandler}>
                    <Text style={stylesFriend.buttonFriendText}>Back to main page</Text>
                </TouchableOpacity>
                <ul>
                    {items.map(item => (
                        <li key={item.id}>
                            {item.name} {item.price}
                        </li>
                    ))}
                </ul>
            </View>
        );
    }
}

const stylesFriend = StyleSheet.create({
    Friend:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriends:{
        backgroundColor:'#0051ff',
        padding: '10px',
        height: '70px',
        width: '70px',
        borderRadius:'50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriendText:{
        color:'#ffffff'
    }
})

export default FriendsScreen;
