const urlAPI = 'http://localhost:5000'
import AsyncStorage from '@react-native-async-storage/async-storage';

const getData = async () => {
   try {
      const value = await AsyncStorage.getItem('accessToken');
      if(value !== null) {
         return value;
      }
   } catch(e) {
      console.log(e);
   }
}

export const requestGetFriends = async () => {
   let response = await fetch(`${urlAPI}/getFriends`);
   if (response.ok) {
      return await response.json();
   } else {
      console.log(response);
      return 0;
   }
}

export const requestFindFriends = async (emailUser, navigaton) => {
   const token = getData();
   const isAuth = onLoggedIn(token);
   alert(isAuth)
   let response = await fetch(`${urlAPI}/findFriend`, {
      method: 'POST',
      headers: {
         'Content-Type': 'application/json;charset=utf-8',
         'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({email: emailUser})
   }).then(async res => {
      try{
            if (res.ok) {
               if(isAuth){
                  console.log(res);
                  alert(res);
                  navigation.navigate('FriendsScreen');
                  return await res.json();
               } else {
                  return 0;
               }
            } else {
               console.log(res);
               return 0;
            }
      } catch (err) {
         console.log(err);
      }
   }).catch(err => {
      console.log(err);
   });
