import React, {useEffect, useState} from "react";
import { View, Text, CheckBox} from "react-native";
import {requestGetFriends} from "../../core/api/API";
import {stylesFriend} from "../../assets/stylesFriends";

const GetFriends = () => {

    const [isLoaded, setIsLoaded] = useState(false);
    const [message, setMessage] = useState(null)
    const [friends, setFriends] = useState([]);
    const [isSelected, setSelection] = useState(false);
    const [error, setError] = useState(null)

    useEffect(async () => {
        setTimeout(async () => {
            await requestGetFriends().then((response) => {
                if (!response) {
                    return
                }
                if (response?.noFriends) {
                    setIsLoaded(true)
                    setMessage("You don't have friends")
                } else if (response?.error) {
                    setMessage(response)
                } else {
                    setFriends(response)
                }
            }).catch(error => {
                setError(error);
            })
        }, 5000)


    }, [])

    return (
        <View>
            {isLoaded ? message ? <span>message</span> : <ul>
                {friends.map(friend => (
                    <li>
                        <CheckBox
                            value={isSelected}
                            onValueChange={setSelection}
                            style={stylesFriend.checkbox}
                        />
                        <Text key={friend.id}>
                            {`${friend.name}, ${friend.email}`}
                        </Text>
                    </li>
                ))}
            </ul> : <span>{error}</span>}
        </View>
    )
}

export default GetFriends
