import React, {useEffect, useState} from "react";
import {View, Text, FlatList} from "react-native";
import {requestGetFriends} from "../../core/api/API";
import Checkbox from "../../assets/checkBox"
import {stylesFriend} from "../../assets/stylesFriends";
//import {Checkbox} from "react-native-paper";

const GetFriends = () => {
    const [isLoaded, setIsLoaded] = useState(false);
    const [message, setMessage] = useState(null)
    const [friends, setFriends] = useState([]);
    const [isSelected, setSelection] = useState(false);
    const [error, setError] = useState(null)
    const [isFriendSelected, setIsFriendSelected] = useState(false)

    useEffect(() => {
        requestGetFriends().then((response) => {
            if (response === 1) {
                setMessage("You don't have friends")
            } else if (response === 0) {
                setMessage("Error")
            } else {
                setFriends(response)
            }
            setIsLoaded(true)
        }).catch(error => {
            setError(error);
        })

    }, [])

    const friendsChecked = [];
//setIsFriendSelected(!isFriendSelected)
    function selectUser(id) {
        let itemIndex = friendsChecked.indexOf(id)
        if(itemIndex !== -1) {
            friendsChecked.splice(itemIndex, 1)
        } else {
            friendsChecked.push(id)
        }
        return friendsChecked
    }

    return (
        <View>
            {isLoaded ? message ? <span>message</span> :
                    <View>
                        {
                            friends.map((item) => (
                                <View style={stylesFriend.friendsList}>
                                <Checkbox onPress={selectUser(item.id)}
                                title={item.email}
                                key={item.id}
                                />
                                </View>
                            ))
                        }
                    </View>
                : <span>{error}</span>}
        </View>
    )
}

/*
<ul>
                {friends.map(friend => (
                    <li>
                        <Checkbox/>
                        <Text key={friend.id}>
                            {`${friend.name}, ${friend.email}`}
                        </Text>
                    </li>
                ))}
            </ul>
* */

export default GetFriends
