/*import React from 'react';
import {
    Image,
    Platform,
    StyleSheet,
    Text,
    TouchableHighlight,
    View
} from 'react-native';

import PropTypes from 'prop-types';

const Checkbox = () => {

    const func =() => {
        if (this.props.checked) {
            this.setState({ checked: true }, () => {
                this.props.checkedObjArr.addItem({
                    'key': this.props.keyValue,
                    'value': this.props.value,
                    'label': this.props.label
                });
            });
        } else {
            this.setState({
                checked: false
            });
        }
    }

    const func2 = (key, label, value) => {
        this.setState({ checked: !this.state.checked }, () => {
            if (this.state.checked) {
                this.props.checkedObjArr.addItem({
                    'key': key,
                    'value': value,
                    'label': label
                });
            } else {
                this.props.checkedObjArr.fetchArray().splice(
                    this.props.checkedObjArr.fetchArray().findIndex(y => y.key == key), 1
                );
            }
        });
    }
        return (
            <TouchableHighlight

                underlayColor="transparent"
                style={{ marginVertical: 20 }}>
                <View style={{
                    flexDirection: 'row',
                    alignItems: 'center' }}>
                    <View style={{
                        padding: 4,
                        width: this.props.size,
                        height: this.props.size,
                        backgroundColor: this.props.color
                    }}>
                        {
                            (this.state.checked)
                                ?
                                (<View style={styles.selectedUI}>
                                    <Image source={require('../public/images/email.png')} style={styles.checkboxTickImg} />
                                </View>)
                                :
                                (<View style={styles.uncheckedCheckbox} />)
                        }
                    </View>
                    <Text style={[styles.checkboxLabel, { color: this.props.labelColor }]}>
                        {this.props.label}
                    </Text>
                </View>
            </TouchableHighlight>
        );

}

Checkbox.propTypes = {
    keyValue: PropTypes.number.isRequired,
    size: PropTypes.number,
    color: PropTypes.string,
    label: PropTypes.string,
    value: PropTypes.string,
    checked: PropTypes.bool,
    labelColor: PropTypes.string,
    checkedObjArr: PropTypes.object.isRequired
}
Checkbox.defaultProps = {
    size: 32,
    checked: false,
    value: 'Default',
    label: 'Default',
    color: '#cecece',
    labelColor: '000000',
}
const styles = StyleSheet.create(
    {
        CheckboxContainer: {
            flex: 1,
            padding: 22,
            alignItems: 'center',
            justifyContent: 'center',
            paddingTop: (Platform.OS === 'ios') ? 25 : 0
        },
        showSelectedButton: {
            padding: 20,
            marginTop: 25,
            alignSelf: 'stretch',
            backgroundColor: '#5D52FF'
        },
        buttonText: {
            fontSize: 20,
            color: '#ffffff',
            textAlign: 'center',
            alignSelf: 'stretch'
        },
        selectedUI: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center'
        },
        checkboxTickImg: {
            width: '85%',
            height: '85%',
            tintColor: '#ffffff',
            resizeMode: 'contain'
        },
        uncheckedCheckbox: {
            flex: 1,
            backgroundColor: '#ffffff'
        },
        checkboxLabel: {
            fontSize: 18,
            paddingLeft: 15
        }
    });

export default Checkbox*/
