import React, {useState} from 'react';
import {
    Platform,
    StyleSheet,
    TouchableHighlight,
    View, Image
} from 'react-native';

import PropTypes from 'prop-types';

const Checkbox = () => {
    const [value, setValue] = useState(false)

    const func =() => {
        if (this.props.checked) {
            this.setState({ checked: true }, () => {
                this.props.checkedObjArr.addItem({
                    'key': this.props.keyValue,
                    'value': this.props.value,
                    'label': this.props.label
                });
            });
        } else {
            this.setState({
                checked: false
            });
        }
    }

    const func2 = (key, value) => {
        this.setState({ checked: !this.state.checked }, () => {
            if (this.state.checked) {
                this.props.checkedObjArr.addItem({
                    'key': key,
                    'value': value,
                    'label': label
                });
            } else {
                this.props.checkedObjArr.fetchArray().splice(
                    this.props.checkedObjArr.fetchArray().findIndex(y => y.key == key), 1
                );
            }
        });
    }
        return (
            <TouchableHighlight>
                <View style={styles.CheckboxContainer}>
                    <Image style={styles.selectSymbol} source={require('../public/images/selectSymbol.png')}/>
                </View>
            </TouchableHighlight>
        );

}
