    checked: PropTypes.bool,
    labelColor: PropTypes.string,
    checkedObjArr: PropTypes.object.isRequired
}
Checkbox.defaultProps = {
    size: 32,
    checked: false,
    value: 'Default',
    label: 'Default',
    color: '#cecece',
    labelColor: '000000',
}*/
const styles = StyleSheet.create(
    {
        CheckboxContainer: {
            display: 'inlineFlex',
            padding: 3,
            alignItems: 'center',
            justifyContent: 'center',
        },
        SelectSymbol: {
            display: 'none'
        }
    });

export default Checkbox
