import React, {useEffect, useState} from 'react';
import {
    StyleSheet,
    View, Pressable, Text, TouchableHighlight
} from 'react-native';

const Checkbox = (props) => {
   /* const [selectColor, setSelectColor] = useState("#fff")
    useEffect(() => {
        if(props.isChecked){
            setSelectColor("#00ff04")
        } else {
            setSelectColor("#fff")
        }
    }, [props.isChecked])*/
    let selectColor = '';// = props.onPress ? "#fff" : "#00ff04";

    let array = props.onPress
    alert(array)
    if(array.indexOf(props.key) !== -1){
        selectColor = "#00ff04"
    } else {
        selectColor = "#fff"
    }

    return (
        <View>
            <TouchableHighlight onPress={props.onPress} style={styles.CheckboxContainer}>
                <View style={{backgroundColor: `${selectColor}`, height: 8, width: 8}} key={props.key}></View>
            </TouchableHighlight>
        <Text>{props.title}</Text>
        </View>
    );
}

const styles = StyleSheet.create(
    {
        CheckboxContainer: {
            display: 'inlineFlex',
            border: '3px solid green',
            height: '15px',
            width: '15px',
            padding: 2,
            alignItems: 'center',
            justifyContent: 'center'
        },
    });

