import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity, CheckBox} from "react-native";
//import {FlatList} from "react-native-gesture-handler";

const FriendsScreen = ({navigation}) => {
    const buttonBackHandler = () => {
        navigation.navigate('MainScreen');
    }

    const [error, setError] = useState(null);
    const [message, setMessage] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [friends, setFriends] = useState([]);
    const [isSelected, setSelection] = useState(false);
    const [text, onChangeText] = useState("Useless Text");
    const [search, setSearch] = useState(false)
    const [newFriend, setNewFriend] = useState(null)
    const [isNewFriendLoaded, setIsNewFriendLoaded] = useState(null)
    const [isSendRequest, setIsSendRequest] = useState(null)

    useEffect(() => {
        fetch("http://localhost:5000/getFriends")
            .then(res => res.json())
            .then(
                (result) => {
                    if ("error" in result) {
                        setIsLoaded(true);
                        setError(result.error);
                    } else if ("message" in result) {
                        setIsLoaded(true);
                        setMessage(result.message);
                    } else {
                        setIsLoaded(true);
                        setFriends(result);
                        console.log(result);
                    }
                },
            )
    }, [])

    useEffect(() => {
        if(search){
            alert("hello")
            fetch("http://localhost:5000/findFriend")
                .then(res => res.json())
                .then(
                    (result) => {
                        if (result === 0) {
                            setIsNewFriendLoaded(false)
                        } else {
                            setIsNewFriendLoaded(true)
                            setNewFriend(result)
                            console.log(result);
                        }
                    },
                )
            setSearch(false)
        }
    }, [search])

    useEffect(() => {
        fetch("http://localhost:5000/addFriend")
            .then(res => res.json())
            .then(
                (result) => {
                    if (result === 0) {

                    } else {

                    }
                },
            )
    }, [isSendRequest])


    if (error) {
        return <View><Text>Ошибка: {error}</Text></View>;
    } else if (!isLoaded) {
        return <View><Text>Загрузка...</Text></View>;
    } else if(message) {
        return <View><Text>Сообщение: {message}</Text></View>;
    } else {
        return (
            <View style={stylesFriend.Friend}>
                <Text>Friends Page</Text>
                <TouchableOpacity style={stylesFriend.buttonFriends} onPress={buttonBackHandler}>
                    <Text style={stylesFriend.buttonFriendText}>Back to main page</Text>
                </TouchableOpacity>
                <form onSubmit={() => {setSearch(true)}}>
                    <label>
                        Find Friend
                        <input type="text" value={text} onChange={onChangeText}/>
                    </label>
                    <input type="submit" value="Find"/>
                </form>
                {isNewFriendLoaded ? <ul>
                    {newFriend.map(newItem => (
                        <li>
                            <form onSubmit={() => {setIsSendRequest(true)}}>
                                <label>
                                    {`${newItem.name}`}
                                    <input type="text" value={"hello"}/>
                                </label>
                                <input type="submit" value="Add Friend"/>
                            </form>
                        </li>
                    ))}
                </ul> : <span>friend not found</span>}
                {isLoaded ? <ul>
                    {friends.map(friend => (
                        <li>
                        <CheckBox
                            value={isSelected}
                            onValueChange={setSelection}
                            style={stylesFriend.checkbox}
                        />
                            <Text key={friend.id}>
                                {`${friend.name}, ${friend.email}`}
                            </Text>
                        </li>
                    ))}
                </ul> : <span>Loading...</span>}

            </View>
        );
    }
}

const stylesFriend = StyleSheet.create({
    Friend:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    inputField:{
        padding:'5px',
        margin: '20px',
        border: '2px solid #000'
    },
    buttonFriends:{
        backgroundColor:'#0051ff',
        padding: '10px',
        height: '70px',
        width: '70px',
        borderRadius:'50%',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriendText:{
        color:'#ffffff'
    },
    checkbox: {
        alignSelf: "center",
    }
})

export default FriendsScreen;
