const urlAPI = 'http://localhost:5000'

export const requestGetFriends = (id) => {
    fetch(`${urlAPI}/getFriends/${id}`)
    .then(res => res.json())
    .then(
        (result) => {
            if ("error" in result) {
                return {error: result}
            } else if (result === 0) {
                return {noFriends: 0}
            } else {
                return result
            }
        },
    )}
