import React, {useEffect, useState} from "react";
import {StyleSheet, View, Text, Button, TouchableOpacity, CheckBox} from "react-native";
//import {FlatList} from "react-native-gesture-handler";

const FriendsScreen = ({navigation}) => {
    const buttonBackHandler = () => {
        navigation.navigate('MainScreen');
    }

    const [error, setError] = useState(null);
    const [message, setMessage] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [friends, setFriends] = useState([]);
    const [isSelected, setSelection] = useState(false);


    useEffect(() => {
        fetch("http://localhost:5000/getFriends")
            .then(res => res.json())
            .then(
                (result) => {
                    if ("error" in result) {
                        setIsLoaded(true);
                        setError(result.error);
                    } else if ("message" in result) {
                        setIsLoaded(true);
                        setMessage(result.message);
                    } else {
                        setIsLoaded(true);
                        setFriends(result);
                        console.log(result);
                    }
                },
            )
    }, [])




    /*if (error) {
        return <View><Text>Ошибка: {error}</Text></View>;
    } else if (!isLoaded) {
        return <View><Text>Загрузка...</Text></View>;
    } else if(message) {
        return <View><Text>Сообщение: {message}</Text></View>;
    } else {*/
        return (
            <View style={stylesFriend.Friend}>
                <Text>Friends Page</Text>
                <View style={stylesFriend.butFriendsWrapper}>
                    <TouchableOpacity style={stylesFriend.buttonFriends} onPress={buttonBackHandler}>
                        <Text style={stylesFriend.buttonFriendsText}>Back to main page</Text>
                    </TouchableOpacity>
                </View>
                <View>
                    {isLoaded ? <ul>
                        {friends.map(friend => (
                            <li>
                            <CheckBox
                                value={isSelected}
                                onValueChange={setSelection}
                                style={stylesFriend.checkbox}
                            />
                                <Text key={friend.id}>
                                    {`${friend.name}, ${friend.email}`}
                                </Text>
                            </li>
                        ))}
                    </ul> : <span>Loading...</span>}
                </View>
            </View>
        );
    //}
}

const stylesFriend = StyleSheet.create({
    Friend:{
        display: 'flex',
        flexDirection: 'column',
        width: '100%',
        height: '100%',
        justifyContent: 'start',
        alignItems: 'center',
        position:'relative'
    },
    inputField:{
        padding:'5px',
        margin: '20px',
        border: '2px solid #000'
    },
    butFriendsWrapper:{
        display: 'flex',
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center'
    },
    buttonFriends:{
        backgroundColor:'fff',
        border: '3px solid #9d0cff',
        borderRadius: '15px',
        boxSizing: 'borderBox',
        minWidth: '120px',
        fontSize:'20px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonFriendsText:{
        padding: '10px',
        color: '#000',
        fontSize: '30px'
    },
    checkbox: {
        alignSelf: "center",
    }
})

export default FriendsScreen;
